package com.saya.recyclerview

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.saya.recyclerview.adapter.ActorAdapter
import com.saya.recyclerview.model.Actors

class MainActivity : AppCompatActivity() {
    private var lstActors = ArrayList<Actors>()
    private lateinit var recyclerView: RecyclerView


    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)
        recyclerView = findViewById(R.id.recyclerView)

        //Fill ArrayList with Actors
        loadActors()
        val adapter = ActorAdapter(lstActors,this@MainActivity)
        recyclerView.layoutManager = LinearLayoutManager(this@MainActivity)
        recyclerView.adapter = adapter
    }

    private fun loadActors(){
        lstActors.add(Actors(1,"Kiran Rana",
                "https://softwarica.edu.np/wp-content/uploads/2019/02/Kiran-Rana.jpg",
        "Kathmandu", 1000000F))

        lstActors.add(Actors(2,"Dayahang Rai",
                "https://upload.wikimedia.org/wikipedia/commons/2/29/Dayahang_Rai_at_ITF_%28cropped%29.jpg",
                "Lalitpur", 7000000F))

        lstActors.add(Actors(3,"saugat Malla",
                "data:image/jpeg;base64,",
                "Lalitpur", 6600000F))

        lstActors.add(Actors(4,"Rajesh hamal",
                "https://pbs.twimg.com/media/EYteSjvUYAEhYfJ.jpg",
                "Lalitpur", 4000000F))
    }
}